example.code:

var appVeyorSettings =
    new AppVeyorBuildSettings
    {
        MessagePattern = "Project: {ProjectName}, File: {FilePath}, Line: {Line}"
    };

ReportCodeAnalysisIssuesToPullRequest(
    MsBuildCodeAnalysis(
        @"c:\build\msbuild.log",
        MsBuildXmlFileLoggerFormat),
    AppVeyorBuilds(appVeyorSettings),
    @"c:\repo");
